:root {
    --color-fg-primary: #000;
    --color-fg-secondary: #333;
    --color-bg-tertiary: #f6f7f8;
    --color-fg-absent: #cc0000;
    --color-fg-h6: #777;
    --color-border-h1: #ddd;
    --color-border-h2: #eee;
}

.markdown-body {
    font-size: 15px;
    font-family: BlinkMacSystemFont, Segoe UI, Helvetica, Arial, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol;
    line-height: 1.5;
    overflow: hidden;
    word-wrap: break-word
}

.markdown-body>*:first-child {
    margin-top: 0 !important
}

.markdown-body>*:last-child {
    margin-bottom: 0 !important
}

.markdown-body a.absent {
    color: var(--color-fg-absent);
}

.markdown-body a.anchor {
    display: block;
    padding-right: 6px;
    padding-left: 30px;
    margin-left: -30px;
    cursor: pointer;
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0
}

.markdown-body a.anchor:focus {
    outline: none
}

.markdown-body h1, .markdown-body h2, .markdown-body h3, .markdown-body h4, .markdown-body h5, .markdown-body h6 {
    margin: 1em 0 15px;
    padding: 0;
    font-weight: bold;
    line-height: 1.7;
    cursor: text;
    position: relative
}

.markdown-body h1 .octicon-link, .markdown-body h2 .octicon-link, .markdown-body h3 .octicon-link, .markdown-body h4 .octicon-link, .markdown-body h5 .octicon-link, .markdown-body h6 .octicon-link {
    display: none;
    color: var(--color-fg-primary);
}

.markdown-body h1:hover a.anchor, .markdown-body h2:hover a.anchor, .markdown-body h3:hover a.anchor, .markdown-body h4:hover a.anchor, .markdown-body h5:hover a.anchor, .markdown-body h6:hover a.anchor {
    text-decoration: none;
    line-height: 1;
    padding-left: 8px;
    margin-left: -30px;
    top: 15%
}

.markdown-body h1:hover a.anchor .octicon-link, .markdown-body h2:hover a.anchor .octicon-link, .markdown-body h3:hover a.anchor .octicon-link, .markdown-body h4:hover a.anchor .octicon-link, .markdown-body h5:hover a.anchor .octicon-link, .markdown-body h6:hover a.anchor .octicon-link {
    display: inline-block
}

.markdown-body h1 tt, .markdown-body h1 code, .markdown-body h2 tt, .markdown-body h2 code, .markdown-body h3 tt, .markdown-body h3 code, .markdown-body h4 tt, .markdown-body h4 code, .markdown-body h5 tt, .markdown-body h5 code, .markdown-body h6 tt, .markdown-body h6 code {
    font-size: inherit
}

.markdown-body h1 {
    font-size: 2.5em;
    border-bottom: 1px solid var(--color-border-h1);
}

.markdown-body h2 {
    font-size: 2em;
    border-bottom: 1px solid var(--color-border-h2);
}

.markdown-body h3 {
    font-size: 1.5em
}

.markdown-body h4 {
    font-size: 1.2em
}

.markdown-body h5 {
    font-size: 1em
}

.markdown-body h6 {
    color: var(--color-fg-h6);
    font-size: 1em
}

.markdown-body p, .markdown-body blockquote, .markdown-body ul, .markdown-body ol, .markdown-body dl, .markdown-body table, .markdown-body pre {
    margin: 15px 0
}

.markdown-body hr {
    background-color: var(--color-border-h1);
    border: 0;
    height: 2px;
    margin: 24px 0;
    padding: 0;
}

.markdown-body ul, .markdown-body ol {
    padding-left: 30px
}

.markdown-body ul.no-list, .markdown-body ol.no-list {
    list-style-type: none;
    padding: 0
}

.markdown-body ul ul, .markdown-body ul ol, .markdown-body ol ol, .markdown-body ol ul {
    margin-top: 0;
    margin-bottom: 0
}

.markdown-body dl {
    padding: 0
}

.markdown-body dl dt {
    font-size: 14px;
    font-weight: bold;
    font-style: italic;
    padding: 0;
    margin-top: 15px
}

.markdown-body dl dd {
    margin-bottom: 15px;
    padding: 0 15px
}

.markdown-body blockquote {
    border-left: 4px solid var(--color-border-h1);
    padding: 0 15px;
    color: var(--color-fg-h6);
}

.markdown-body blockquote>:first-child {
    margin-top: 0px
}

.markdown-body blockquote>:last-child {
    margin-bottom: 0px
}

.markdown-body table {
    width: 100%;
    overflow: auto;
    display: block
}

.markdown-body table th {
    font-weight: bold
}

.markdown-body table th, .markdown-body table td {
    border: 1px solid var(--color-border-h1);
    padding: 6px 13px
}

.markdown-body table tr {
    border-top: 1px solid var(--color-border-h1);
    background-color: #fff
}

.markdown-body table th {
    /* .markdown-body table tr:nth-child(2n) */
    background-color: var(--color-bg-tertiary);
}

.markdown-body img {
    max-width: 100%;
    box-sizing: border-box
}

.markdown-body span.frame {
    display: block;
    overflow: hidden
}

.markdown-body span.frame>span {
    border: 1px solid var(--color-border-h1);
    display: block;
    float: left;
    overflow: hidden;
    margin: 13px 0 0;
    padding: 7px;
    width: auto
}

.markdown-body span.frame span img {
    display: block;
    float: left
}

.markdown-body span.frame span span {
    clear: both;
    color: var(--color-fg-secondary);
    display: block;
    padding: 5px 0 0
}

.markdown-body span.align-center {
    display: block;
    overflow: hidden;
    clear: both
}

.markdown-body span.align-center>span {
    display: block;
    overflow: hidden;
    margin: 13px auto 0;
    text-align: center
}

.markdown-body span.align-center span img {
    margin: 0 auto;
    text-align: center
}

.markdown-body span.align-right {
    display: block;
    overflow: hidden;
    clear: both
}

.markdown-body span.align-right>span {
    display: block;
    overflow: hidden;
    margin: 13px 0 0;
    text-align: right
}

.markdown-body span.align-right span img {
    margin: 0;
    text-align: right
}

.markdown-body span.float-left {
    display: block;
    margin-right: 13px;
    overflow: hidden;
    float: left
}

.markdown-body span.float-left span {
    margin: 13px 0 0
}

.markdown-body span.float-right {
    display: block;
    margin-left: 13px;
    overflow: hidden;
    float: right
}

.markdown-body span.float-right>span {
    display: block;
    overflow: hidden;
    margin: 13px auto 0;
    text-align: right
}

.markdown-body code, .markdown-body pre, .markdown-body tt {
    border-radius: 3px;
    font-size: 0.85rem;
}

.markdown-body code:not(.hljs) {
    padding: .2em 0em;
    background-color: var(--color-bg-tertiary);
}

.markdown-body code:before, .markdown-body code:after, .markdown-body tt:before, .markdown-body tt:after {
    content: "\00a0";
    letter-spacing: -0.2em
}

.markdown-body code br, .markdown-body tt br {
    display: none
}

.markdown-body del code {
    text-decoration: inherit;
    vertical-align: text-top
}

.markdown-body pre {
    overflow: auto;
    word-wrap: normal;
    white-space: pre-wrap;
}

.markdown-body pre tt {
    margin: 0;
    padding: 0;
    border: none;
    word-wrap: normal;
    max-width: initial;
    display: inline;
    overflow: initial;
    line-height: inherit;
}

.markdown-body pre code:before, .markdown-body pre code:after, .markdown-body pre tt:before, .markdown-body pre tt:after {
    content: normal;
}

.hljs-github .hljs {
    color: var(--color-fg-secondary);
    background-color: var(--color-bg-tertiary);
}

.hljs {
    display: block;
    overflow-x: auto;
    padding: 0.5em;
}

html {
  box-sizing: border-box;
}
*, *:before, *:after {
  box-sizing: inherit;
}
body {
  max-width: 900px;
  margin: 0 auto;
  background: #fff;
  padding: 1em 4em;
  border-right: 1px solid #d1d5da;
  border-left: 1px solid #d1d5da;
  min-height: 100vh;
}
img {
  max-width: 100%;
}
a, a:active {
  color:#0366d6;
  text-decoration: none;
}
a[name] {
  color: unset;
}
a.top {
  float: right;
  margin-right: 10px;
  text-decoration: none;
  color: #ccc;
}
.markdown-body table {
  border-collapse: collapse;
}
.markdown-body {
  word-wrap: break-word;
}
.markdown-body .task-list-item {
  list-style: none;
}
.markdown-body .task-list-item-checkbox {
  margin: 0 0.2em 0.25em -1.6em;
  vertical-align: middle;
}

kbd {
  background-color: #eee;
  border-radius: 3px;
  border: 1px solid #b4b4b4;
  box-shadow: 0 1px 1px rgba(0, 0, 0, .2), 0 2px 0 0 rgba(255, 255, 255, .7) inset;
  color: #333;
  display: inline-block;
  font-size: .85em;
  font-weight: 700;
  line-height: 1;
  padding: 2px 4px;
  white-space: nowrap;
}

x-error {
  background: none repeat scroll 0 0 #f00;
  color: #fff;
  display: block;
  font-weight: bold;
  padding: 0.5em;
  text-align: center;
  width: 100%;
}
x-note {
  display: block;
  padding: 0.5em;
  background-color: #ff6;
  box-shadow: 0px 0px 5px 2px #ff0
}

@media print {
  html {
    background: initial;
  }
  body {
    max-width: unset;
    margin: unset;
    background: #fff;
    box-shadow: unset;
    border: unset;
  }
}

